import{_ as i,c as a,o,ae as l}from"./chunks/framework.Bl2MNIgY.js";const g=JSON.parse('{"title":"VibeCoding Manual","description":"","frontmatter":{"title":"VibeCoding Manual","layout":"doc","outline":"deep"},"headers":[],"relativePath":"index.md","filePath":"index.md","lastUpdated":1759078368000}'),d={name:"index.md"};function t(n,e,r,c,s,u){return o(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="vibecoding-manual" tabindex="-1">VibeCoding Manual <a class="header-anchor" href="#vibecoding-manual" aria-label="Permalink to &quot;VibeCoding Manual&quot;">​</a></h1><nav class="table-of-contents"><ul><li><a href="#はじめに">はじめに</a></li><li><a href="#クイックスタート">クイックスタート</a></li><li><a href="#サイト構成">サイト構成</a></li><li><a href="#主要ドキュメントへの導線">主要ドキュメントへの導線</a></li><li><a href="#github-pages-公開フロー">Github Pages 公開フロー</a></li><li><a href="#更新のすすめ">更新のすすめ</a></li></ul></nav><h2 id="はじめに" tabindex="-1">はじめに <a class="header-anchor" href="#はじめに" aria-label="Permalink to &quot;はじめに&quot;">​</a></h2><p>このマニュアルは vibecoding プロジェクトで開発・運用を進めるメンバーの共通ハブです。初回セットアップから日々のワークフロー、レビュー基準までを１か所で確認できます。まずは下記のクイックスタートに沿って環境を整え、必要な章へ進んでください。</p><h2 id="クイックスタート" tabindex="-1">クイックスタート <a class="header-anchor" href="#クイックスタート" aria-label="Permalink to &quot;クイックスタート&quot;">​</a></h2><ol><li>リポジトリをクローンし、<code>npm install</code> で依存関係を導入します。</li><li>ドキュメント編集時は <code>npm run docs:dev</code> を実行してホットリロード環境を起動します。</li><li>執筆・レビューが完了したら <code>npm run docs:build</code> でビルドを通し、Pull Request に実行コマンドと確認結果を記載します。</li><li>GitHub Pages 公開フローは「<a href="#github-pages-公開フロー">デプロイ手順</a>」を参照し、<code>main</code> から生成した内容を <code>gh-pages</code> へ反映します。</li></ol><h2 id="サイト構成" tabindex="-1">サイト構成 <a class="header-anchor" href="#サイト構成" aria-label="Permalink to &quot;サイト構成&quot;">​</a></h2><ul><li><code>ホーム (このページ)</code> : 主要リンク、ワークフローの概要、最新の運用ルール。</li><li><code>ガイド</code> : <code>/guide/intro</code> から順に読むと背景や考え方が把握できます。</li><li><code>リファレンス</code> : ツール設定やテンプレート、トラブルシューティングを集約しています。</li><li>サイドバーは章別に整理されているため、初学者は「入門」セクションから、実務で迷ったときは「実践」セクションを参照してください。</li></ul><h2 id="主要ドキュメントへの導線" tabindex="-1">主要ドキュメントへの導線 <a class="header-anchor" href="#主要ドキュメントへの導線" aria-label="Permalink to &quot;主要ドキュメントへの導線&quot;">​</a></h2><ul><li><a href="/vibecoding-intro/guide/intro">vibecodingとは</a> — プロジェクトの目的と共通原則。</li><li><a href="/vibecoding-intro/guide/how-to-use">マニュアルの読み方</a> — 学習順序と更新フロー。</li><li><a href="/vibecoding-intro/guide/style-guide">コーディングスタイル</a> — 命名規則やレビュー観点。</li><li><a href="/vibecoding-intro/guide/review">レビューとフィードバック</a> — Pull Request 運用のベストプラクティス。</li><li><a href="/vibecoding-intro/guide/faq">FAQ</a> — よくある質問と対応策。</li><li><a href="/vibecoding-intro/guide/reference">リファレンス</a> — Codex CLI やテンプレート類の詳細手順。</li></ul><h2 id="github-pages-公開フロー" tabindex="-1">Github Pages 公開フロー <a class="header-anchor" href="#github-pages-公開フロー" aria-label="Permalink to &quot;Github Pages 公開フロー&quot;">​</a></h2><ol><li><code>main</code> ブランチで最新のドキュメントを更新し、ビルドを通します。</li><li>CI/CD で <code>npm run docs:build</code> の成果物が <code>docs/.vitepress/dist</code> に生成されることを確認し、<code>actions/deploy-pages</code> が <code>gh-pages</code> ブランチへデプロイします。</li><li>初回のみ GitHub の Settings &gt; Pages で公開元を <code>gh-pages</code> ブランチ／<code>root</code> に設定し、カスタムドメインがある場合は DNS を調整します。</li><li>デプロイに失敗したときは Actions のログでエラーを確認し、再実行または Secrets（例: <code>DOCS_BASE</code>）の設定漏れを修正してから再デプロイします。</li></ol><h2 id="更新のすすめ" tabindex="-1">更新のすすめ <a class="header-anchor" href="#更新のすすめ" aria-label="Permalink to &quot;更新のすすめ&quot;">​</a></h2><ul><li>各章を編集したらこのページのリンクも見直し、必要に応じて導線を増やしてください。</li><li>重要な決定事項は <code>docs/templates/</code> にドラフトを作成し、レビュー後このページからリンクします。</li><li>定期的に「<a href="/vibecoding-intro/guide/reference">リファレンス</a>」のトラブルシューティングを最新化し、セットアップ手順が現行の CLI バージョンと一致しているか確認しましょう。</li></ul>',14)])])}const b=i(d,[["render",t]]);export{g as __pageData,b as default};
